import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import frontpage from './frontPage.png'; // Import the front page image

function LoginPage() {
    const [isLogin, setIsLogin] = useState(true);
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [message, setMessage] = useState('');
    const navigate = useNavigate(); // React Router hook to navigate

    const toggleForm = () => {
        setIsLogin(!isLogin);
    };

    const handleSubmit = async (event) => {
        event.preventDefault();

        const url = '/api/login'; // We're focusing on the login functionality here
        const requestData = { email, password };

        try {
            const response = await fetch(url, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(requestData),
            });

            if (response.ok) {
                navigate('/'); // Redirect to home page on success
            } else {
                const error = await response.text();
                setMessage(`Error: ${error}`); // Show error message
            }
        } catch (error) {
            setMessage(`Error: ${error.message}`); // Handle network errors
        }
    };

    return (
        <div className="login-page-container"> {/* Add this wrapper */}
            <div className="auth-container">
                <h1>Login</h1>
                <form className="auth-form" onSubmit={handleSubmit}>
                    <div className="form-group">
                        <label>Email:</label>
                        <input
                            type="email"
                            name="email"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            required
                        />
                    </div>
                    <div className="form-group">
                        <label>Password:</label>
                        <input
                            type="password"
                            name="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            required
                        />
                    </div>
                    <button type="submit" className="auth-btn">Login</button>
                </form>
                <button onClick={toggleForm} className="toggle-form-btn">
                    {isLogin ? 'Sign Up' : 'Login'}
                </button>
                {message && <p>{message}</p>}
            </div>
        </div>
    );
}

export default LoginPage;
